package com.emoney2.exception;import lombok.AllArgsConstructor;import lombok.Data;import lombok.EqualsAndHashCode;import org.springframework.http.HttpStatus;import org.springframework.web.bind.annotation.ResponseStatus;/** * @author Shalom Olomolaiye * @philosophy It's not "CODE" It's how you think... Think and solve the problem * Growth is when you keep finding efficient ways to solve the problem * <p> * ------ * Tip: Always code as if the guy who ends up maintaining your code will be a violent psychopath who knows where you live. * 'Cause he is violent,and he knows where you live. * ------ * Anyway, welcome to the FileOperationException class... HAPPY THINKING * @since 21/04/2022 1:29 PM */@Data@AllArgsConstructor@ResponseStatus(value = HttpStatus.EXPECTATION_FAILED, reason = "Something went wrong!")@EqualsAndHashCode(callSuper = false)public class ProcessorException extends RuntimeException {    private int status;    /**     *     */    private static final long serialVersionUID = -4915936257141907508L;    public ProcessorException(String message, int status) {        super(message);        this.status = status;    }}